		private Pattern patternTag;
		private Matcher matcherTag;

		@Override
		public void map(Text key, ArchiveReader value, Context context) throws IOException {
			// Compile the regular expression once as it will be used continuously
			patternTag = Pattern.compile(EMAIL_PATTERN);
			LOG.info("In map task ossdfdsgsfnjdzgsdgdbhfgsdfghrdgr");

			Iterator<ArchiveRecord> i = value.iterator();

			while (i.hasNext()) {
				ArchiveRecord r = i.next();
				LOG.info(r);
				LOG.info(r.getHeader().getHeaderFields());
				byte[] b = IOUtils.toByteArray(r);
				LOG.info(b);
			}
			
			for (ArchiveRecord r : value) {
				LOG.info("Record tasclaslfsbkj"+r);
					try {

					if (r.getHeader().getMimetype().equals("application/http; msgtype=response")) {
						// Convenience function that reads the full message into a raw byte array

						byte[] rawData = IOUtils.toByteArray(r);
						String content = new String(rawData);
						System.out.println(content);
						LOG.info(content);
						// The HTTP header gives us valuable information about what was received during the request
//						String headerText = content.substring(0, content.indexOf("\r\n\r\n"));

						// TODO: Proper HTTP header parsing + don't trust headers
							// We're only interested in processing the responses, not requests or metadata
//							String url = r.getHeader().getUrl();
							matcherTag = patternTag.matcher(content);
							while (matcherTag.find()) {
								context.write(new Text(matcherTag.group(0)), outVal);
							}
//						}
					}
				}
				catch (Exception ex) {

				}
			}
		}
